import { Link } from "react-router-dom";

export default function ProductCard(props) {
  const { productData, btnName, handleClick , handleNavigate } = props;
  return (
    <div className="card h-100">
      <img src={productData.image} className="card-img-top" alt="..." />
      <div className="card-body">
        {productData.onSale ? (
          <span class="badge text-bg-success">On Sale</span>
        ) : (
          <span class="badge text-bg-secondary">Original Price</span>
        )}
        <Link to={`/game-details/${productData.id}`}>
          <h5 className="card-title">{productData.title}</h5>
        </Link>
        <p className="card-text">{productData.description}</p>
        {productData.tags.map((tag) => (
          <span className="badge text-bg-primary">{tag}</span>
        ))}
         <button
          className="d-block mt-3 btn btn-info"
          onClick={() => handleNavigate(productData.id)}
        >
          View
        </button>
        <button
          className="d-block mt-3 btn btn-danger"
          onClick={() => handleClick(productData.id)}
        >
          {btnName}
        </button>
      </div>
      <div className="card-footer">
        <small className="text-body-secondary">
          Price : {productData.price} $
        </small>
      </div>
    </div>
  );
}
