import { useEffect, useState } from "react";
import GameCard from "./GameCard";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { axiosInstance } from "../../apis/config";
import { getOldGamesList } from "../../apis/games";

const GamesList = () => {
  const navigate = useNavigate();
  const [gamesList, setGamesList] = useState([]);

  useEffect(() => {
    axiosInstance
      .get("/gamehub", {
        params: {
          name: "test",
        },
      })
      .then((res) => setGamesList(res.data))
      .catch((err) => console.log(err));

    // axiosInstance
    //   .get("/old-games")
    //   .then((res) => console.log(res))
    //   .catch((error) => console.log(error));

    getOldGamesList()
      .then((res) => console.log(res))
      .catch((err) => console.log(err));
  }, []);

  const deleteGame = (id) => {
    console.log(id);
    const updatedList = gamesList.filter((game) => game.id !== id);
    setGamesList(updatedList);
  };

  const redirectToDetails = (id) => {
    navigate(`/game-details/${id}`);
  };

  return (
    <div className="row row-cols-1 row-cols-md-3 g-4">
      {gamesList.map((game, index) => {
        return (
          <div className="col" key={game.id}>
            <GameCard
              gameData={game}
              btnName={"Delete Game"}
              handleClick={(id) => deleteGame(id)}
              handleNavigate={(id) => redirectToDetails(id)}
            />
          </div>
        );
      })}
    </div>
  );
};

export default GamesList;

