import { useEffect, useState } from "react";
import ProductCard from "./productCard";
import { useNavigate } from "react-router-dom";
import axios from 'axios'
import { useDispatch, useSelector } from "react-redux";
import {increaseCart} from "../../store/slices/cart";

const ProductsList = () => {
  const navigate = useNavigate();
  const [productList, setProductList] = useState([]);
  const [cartedProduct, setCartedProductList] = useState([]);
  const cart = useSelector((state) => state.cart.cartContentAmount);
  const dispatch = useDispatch();

  useEffect(() => {
    axios
      .get(`https://dummyjson.com/products/`)
      .then((res) => setProductList(res.data.products) )
      .catch((err) => console.log(err));
  }, []);

  const redirectToDetails = (id) => {
    navigate(`/product-details/${id}`);
  };

  const addToCart = (product) => {
    setCartedProductList(cartedProduct => [...cartedProduct, product])
  };

  return (
    <div className="row row-cols-1 row-cols-md-3 g-4">
      {productList?.map((product, index) => {
        return (
          <div className="col" key={product.id}>
            <ProductCard
              productData={product}
              handleNavigate={redirectToDetails}
              addToCat={dispatch(increaseCart())}
            />
          </div>
        );
      })}
    </div>
  );
};

export default ProductsList;
