import React, { useState } from 'react'

export default function Register() {
  const [register, setRegister] = useState({
    name: "",
    email: "",
    userName:'',
    password:''
  });
  const [registerErr, setRegisterErr] = useState({
    name:null,
    email:null,
    userName:null,
    password:null
  });
  const handleFormChange = (event) => {
    if (event.target.name === "name") {
      setRegister({
        ...register,
        name: event.target.value,
      });

      setRegisterErr({
        ...registerErr,
        name:
          event.target.value.length === 0
            ? "This field is required"
            : event.target.value.length < 3
            ? "Min.length is 3"
            : null,
      });
    }

    if (event.target.name === "email") {
      setRegister({
        ...register,
        email: event.target.value,
      });

      setRegisterErr({
        ...registerErr,
        email:
          event.target.value.length === 0
            ? "This field is required"
            : e.target.value == 0
            ? "Value must be greater than 0"
            : null,
      });
    }

    if (event.target.name === "username") {
      setRegister({
        ...register,
        username: event.target.value,
      });

      setRegisterErr({
        ...registerErr,
        username:
          event.target.value.length === 0
            ? "This field is required"
            : e.target.value == 0
            ? "Value must be greater than 0"
            : null,
      });
    }

    if (event.target.name === "password") {
      setRegister({
        ...register,
        password: event.target.value,
      });
    }
  };

  return (
    <>
      <h1>Register</h1><hr/>
      <form>
        <div className="form-group">
          <label for="name">Name</label>
          <input type="text" className="form-control" name="name" placeholder="your name"/>
        </div>
        <div className="form-group">
          <label for="email">Email address</label>
          <input type="email" className="form-control" name="email" aria-describedby="emailHelp" placeholder="your email"/>
        </div>
        <div className="form-group">
          <label for="username">User name</label>
          <input type="text" className="form-control" name="username" placeholder="your user name"/>
        </div>
        <div className="form-group">
          <label for="password">Password</label>
          <input type="password" className="form-control" name="password" placeholder="Password"/>
        </div>
        <div className="form-group">
          <label for="confirmPassword">Confirm password</label>
          <input type="password" className="form-control" name="confirmPassword" placeholder="Confirm password"/>
        </div>
        <button type="submit" className="btn btn-primary m-5">Register</button>
      </form>
    </>
  )
}
