import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import { NavLink } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faShopify } from '@fortawesome/free-brands-svg-icons';
import { useSelector } from "react-redux";
import { LanguageContext } from '../../context/language';
import { useContext } from "react";

function HeaderNav() {
  const { contextLang , setContextLang }  = useContext(LanguageContext)
  const cart = useSelector((state) => state.cart.cartContentAmount);
  return (
    <Navbar className="bg-primary-subtle">
      <Container>
        <Navbar.Brand className={contextLang === "EN" ? "text-danger" : "text-primary"}
        dir={contextLang === "EN" ? "rtl" : "ltr"}>Products App</Navbar.Brand>
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="ms-auto">
            <NavLink className='nav-link my-auto me-5'>
              <select className="form-select " aria-label="Default select example" onChange={(event) => setContextLang(event.target.value)}>
                <option value="EN">EN</option>
                <option value="AR">AR</option>
                <option value="FR">FR</option>
              </select>
            </NavLink>
            <NavLink className='nav-link my-auto' 
            style={({ isActive }) => (isActive ? {color: 'green', textDecoration : 'underline'} : {color: 'black'})} 
            to='/'>Home
            </NavLink>
            <NavLink className='nav-link my-auto' 
            style={({ isActive }) => (isActive ? {color: 'green', textDecoration : 'underline'} : {color: 'black'})} 
            to="/register">Register
            </NavLink>
            <NavLink className='nav-link my-auto' 
            style={({ isActive }) => (isActive ? {color: 'green', textDecoration : 'underline'} : {color: 'black'})} 
            to="/login">Login
            </NavLink>
            <NavLink className='nav-link fs-1 my-auto' 
            style={({ isActive }) => (isActive ? {color: 'green'} : {color: 'black'})} 
            to="/cart">
              <div className='position-relative' style={{width:'50px', height:'50px'}}>
                <h1 className='w-25 h-25  position-absolute text-danger fs-5 end-0 top-0'>{cart}</h1>
                <FontAwesomeIcon icon={faShopify} />
              </div>
            </NavLink>
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
}

export default HeaderNav;
