import { Link } from "react-router-dom";

export default function GameCard(props) {
  const { gameData, btnName, handleClick , handleNavigate } = props;
  return (
    <div className="card h-100">
      <img src={gameData.image} className="card-img-top" alt="..." />
      <div className="card-body">
        {/* {game.onSale && <span class="badge text-bg-success">On Sale</span>} */}
        {gameData.onSale ? (
          <span class="badge text-bg-success">On Sale</span>
        ) : (
          <span class="badge text-bg-secondary">Original Price</span>
        )}
        <Link to={`/game-details/${gameData.id}`}>
          <h5 className="card-title">{gameData.title}</h5>
        </Link>
        <p className="card-text">{gameData.description}</p>
        {gameData.tags.map((tag) => (
          <span className="badge text-bg-primary">{tag}</span>
        ))}
         <button
          className="d-block mt-3 btn btn-info"
          onClick={() => handleNavigate(gameData.id)}
        >
          View
        </button>
        <button
          className="d-block mt-3 btn btn-danger"
          onClick={() => handleClick(gameData.id)}
        >
          {btnName}
        </button>
      </div>
      <div className="card-footer">
        <small className="text-body-secondary">
          Price : {gameData.price} $
        </small>
      </div>
    </div>
  );
}
