import { Link } from "react-router-dom";
export default function ProductCard(props) {

  const { productData, handleNavigate } = props;

  return (
    <div className="card position-relative" style={{height:'400px'}}>
      <div>
      {productData.stock == 0 ? (
          <span class="badge text-bg-danger position-absolute m-2">Out of stock</span>
        ) : (
          <span class="badge text-bg-success position-absolute m-2">In stock</span>
        )}
      </div>
      <img src={productData.thumbnail} className="card-img-top h-50" alt="..." />
      <div className="card-body position-relative">
        <div className="row">
          <p className="card-title col">{productData.title}</p>
          <div className="col position-relative">
            <span className=" position-absolute end-0" >{productData.price} $ </span>
          </div>   
        </div>
        <p className="card-text" style={{textOverflow:'ellipsis',overflow:'hidden',whiteSpace:'nowrap'}}>
          {productData.description}
        </p>
        <span>{productData.rating}</span>
        <button
          className="rounded-5 position-absolute bottom-0 start-0 m-3 btn btn-success"
          
        >
          Add to cart
        </button>
        {/* <Link to= '/product-details/1'> */}
          <button
            className="rounded-5 position-absolute bottom-0 end-0 m-3 btn btn-info"
            onClick={() => handleNavigate(productData.id)}
          >
            View Details
          </button>
        {/* </Link> */}
      </div>
    </div>
    
  );
}
