import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  cartContentAmount: 0,
  cartContentAmountLimit: 50,
  cartedProducts: []
};

const cart = createSlice({
  name: "cart",
  initialState: initialState,
  reducers: {
    increaseCart: (state) => {
      state.cartContentAmount = state.cartContentAmount + 1;
    },
    decreaseCart: (state) => {
      state.cartContentAmount = state.cartContentAmount - 1;
    },
    resetCart: (state) => {
      state.cartContentAmount = 0;
    },
    addProduct: (state, action) => {
      state.cartedProducts.push(action.payload)
    }
  },
});

export const {
    increaseCart,
    decreaseCart,
    resetCart,
} = cart.actions;

export default cart.reducer;
